import React, { useEffect, useState } from "react";
import Link from "next/link";
import { TextField, Button, InputAdornment } from "@material-ui/core";
import { useRouter } from "next/router";
import { useSelector } from "react-redux";
import { Person, Image, Loyalty, AccountCircle } from "@material-ui/icons";
import "../../styles/editpost.css";
import Navbar from "../../components/Navbar";

function Id() {
  const [username, setUsername] = useState("");
  const [image, setImage] = useState("");
  const [caption, setCaption] = useState("");
  const router = useRouter();
  const post = useSelector((state) => state.postReducer.post);

  useEffect(() => {
    console.log(post);
    setUsername(post[0].username);
    setImage(post[0].imageUrl);
    setCaption(post[0].caption);
  }, [post]);

  // useEffect(() => {
  //   console.log(router.query.id);
  //   if (router.query.id) {
  //     dispatch(getPostDetail(router.query.id));
  //   }
  // }, []);

  return (
    <div>
      {JSON.stringify(post)}
      {router.query.id}
      <Navbar />
      <div className="main__editpost">
        <div className="editpost">
          <h1>EDIT Post </h1>
          <form className="form" noValidate autoComplete="off">
            <div className="icons">
              <TextField
                className="form__input"
                id="input-with-icon-textfield"
                label="TextField"
                value={username}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <AccountCircle />
                    </InputAdornment>
                  ),
                }}
              />
            </div>
            <div className="icons">
              <TextField
                className="form__input"
                id="input-with-icon-textfield"
                label="TextField"
                value={image}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <Image />
                    </InputAdornment>
                  ),
                }}
              />
            </div>

            <div className="icons">
              <TextField
                className="form__input"
                id="input-with-icon-textfield"
                label="TextField"
                value={caption}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <Loyalty />
                    </InputAdornment>
                  ),
                }}
              />
            </div>

            <Button variant="contained" color="primary" className="button__add">
              Submit
            </Button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Id;
