export const adding = (username, image, caption) => {
  return (dispatch, getState) => {
    const { posts } = getState;
    console.log(posts);
    dispatch({
      type: "ADD_POST",
      newPost: {
        id: posts.length - 1,
        username,
        image,
        caption,
      },
    });
  };
};
