const defaultState = {
  posts: [
    {
      id: 1,
      username: "jackson",
      imageUrl:
        "https://mmc.tirto.id/image/otf/880x495/2020/04/20/dragon_ball_super_key_visual_ratio-16x9.jpg",
      caption: "Black Live Matter",
      comments: [
        {
          username: "kevin",
          comment: "hello",
        },
      ],
    },
    {
      id: 2,
      username: "clinton",
      imageUrl:
        "https://mmc.tirto.id/image/otf/880x495/2020/04/20/dragon_ball_super_key_visual_ratio-16x9.jpg",
      caption: "Black Live Matter",
      comments: [
        {
          username: "kevin",
          comment: "hello",
        },
      ],
    },
  ],
  post: {},
};
export default function reducer(state = defaultState, action) {
  switch (action.type) {
    case "ADD_POST":
      return { ...state, posts: state.posts.concat(action.newPost) };

    case "DETAIL_POST":
      return {
        ...state,
        post: state.posts.filter((post) => {
          if (post.id == action.id) {
            return post;
          }
        }),
      };

    case "EDIT_POST":
      return {
        ...state,
        post: state.posts.filter((post) => {
          if (post.id == action.editpost.id) {
            post.username = action.editpost.username;
            post.imageUrl = action.editpost.image;
            post.caption = action.editpost.caption;
          }
        }),
      };

    case "DELETE_POST":
      return {
        ...state,
        posts: state.posts.filter((post) => {
          return post.id !== action.id;
        }),
      };

    case "ADD_COMMENT":
      return {
        ...state,
        posts: state.posts.map((post) => {
          if (post.id == action.id) {
            post.comments.push(action.data);
          }
        }),
      };

    default:
      return state;
  }
}
