export const adding = (username, image, caption) => {
  return (dispatch, getState) => {
    const { posts } = getState().postReducer;
    console.log(posts);
    console.log(posts[posts.length - 1].id);
    dispatch({
      type: "ADD_POST",
      newPost: {
        id: posts[posts.length - 1].id + 1,
        username,
        image,
        caption,
      },
    });
  };
};
