import React, { useEffect, useState } from "react";
import Link from "next/link";
import { TextField, Button } from "@material-ui/core";
import { useRouter } from "next/router";
import { useDispatch, useSelector } from "react-redux";
import { getPostDetail } from "../../store/actions/postAction";
import { Person, Image, Loyalty } from "@material-ui/icons";

function Id() {
  const [username, setUsername] = useState("");
  const [image, setImage] = useState("");
  const [caption, setCaption] = useState("");
  const router = useRouter();
  const dispatch = useDispatch();
  const post = useSelector((state) => state.postReducer.post);

  useEffect(() => {
    console.log(router.query.id);
    dispatch(getPostDetail(router.query.id));
  }, []);

  return (
    <div className="main__editpost">
      {JSON.stringify(post[0])}
      <div className="editpost">
        <h1>Add Post to Your Timeline</h1>
        <form className="form" noValidate autoComplete="off">
          <div className="icons">
            <Person className="icon" />
            <TextField
              value={post[0].username}
              className="form__input"
              id="outlined-basic"
              label="Username"
              variant="outlined"
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="icons">
            <Image className="icon" />
            <TextField
              className="form__input"
              id="outlined-basic"
              label="Image Url"
              variant="outlined"
              onChange={(e) => setImage(e.target.value)}
            />
          </div>

          <div className="icons">
            <Loyalty className="icon" />
            <TextField
              className="form__input"
              id="outlined-basic"
              label="Caption"
              variant="outlined"
              onChange={(e) => setCaption(e.target.value)}
            />
          </div>

          <Button variant="contained" color="primary" className="button__add">
            Submit
          </Button>
        </form>
      </div>
    </div>
  );
}

export default Id;
